mapper.select:
SELECT 

FROM t_dictionary_entry

    is_deleted = 0

        AND dictionary_type = #{dictionaryType}


        AND tenant = #{tenant}


        AND channel = #{channel}


        AND domain = #{domain}


        AND entry_key LIKE CONCAT('%', #{entryKey}, '%')


        AND status = #{status}


        AND created_by = #{createdBy}


ORDER BY priority DESC, updated_at DESC

