mapper.insert:
INSERT INTO t_dictionary_entry (
    dictionary_type, tenant, channel, domain, entry_key, 
    entry_value, description, status, priority, version, 
    created_at, updated_at, created_by, updated_by
) VALUES

(
    #{item.dictionaryType}, #{item.tenant}, #{item.channel}, #{item.domain}, #{item.entryKey},
    #{item.entryValue}, #{item.description}, #{item.status}, #{item.priority}, #{item.version},
    #{item.createdAt}, #{item.updatedAt}, #{item.createdBy}, #{item.updatedBy}
)

mapper.update:
UPDATE t_dictionary_entry SET status = #{status}, updated_at = #{updatedAt}, updated_by = #{updatedBy}, version = version + 1 WHERE id IN
#{id}
AND is_deleted = 0

mapper.delete:
DELETE FROM t_dictionary_entry 
WHERE dictionary_type = #{dictionaryType}
AND tenant = #{tenant}
AND channel = #{channel}
AND domain = #{domain}

mapper.update:
UPDATE t_dictionary_entry SET is_deleted = 1, updated_at = #{updatedAt}, updated_by = #{updatedBy}, version = version + 1 WHERE id IN
#{id}
AND is_deleted = 0

mapper.select:
SELECT dictionary_type, tenant, channel, domain, COUNT(*) as entry_count, COUNT(CASE WHEN status = 'ACTIVE' THEN 1 END) as active_count, MAX(updated_at) as last_updated, MAX(version) as max_version FROM t_dictionary_entry WHERE is_deleted = 0
AND dictionary_type = #{dictionaryType}
AND tenant = #{tenant}
AND channel = #{channel}
AND domain = #{domain}
GROUP BY dictionary_type, tenant, channel, domain ORDER BY last_updated DESC
