mapper.select:
SELECT * FROM t_cs_faq WHERE is_deleted = 0
AND (question LIKE CONCAT('%', #{keyword}, '%') OR answer_text LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY updated_at DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE t_cs_faq
SET hit_count = hit_count + 1,
    updated_at = #{updatedAt}
WHERE id = #{id}
  AND is_deleted = 0
